public class Test10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//乘法表
            for(int i=1;i<=9;i++)
            {
            	for(int j=1;j<=i;j++)
            	{
            		System.out.print(i+"*"+j+"="+(i*j)+'\t');
            	}
            	//打空行
            	System.out.println();
            	
            }
		
		
		
	}

}
